custom_css:
/* Example CSS for your font: sfdistantgalaxyalternate-oe4d */

/* Apply to all text */
:root * {
	--font-default: 'sfdistantgalaxyalternate-oe4d';
	--default-font: 'sfdistantgalaxyalternate-oe4d';
	--font-family-editor: 'sfdistantgalaxyalternate-oe4d';
	--font-interface-override: 'sfdistantgalaxyalternate-oe4d';
	--font-text-override: 'sfdistantgalaxyalternate-oe4d';
}

/* Example: Apply to custom CSS class */
.custom-font * {
	font-family: 'sfdistantgalaxyalternate-oe4d' !important;
}

/* Example: Apply to specific elements only */
.custom-font h1, .custom-font h2, .custom-font h3 {
	font-family: 'sfdistantgalaxyalternate-oe4d' !important;
}
